import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { fetchSpotifyData, fetchYoutubeData } from '../api';

const Dashboard = ({ spotifyToken, youtubeToken }) => {
  const [spotifyData, setSpotifyData] = useState([]);
  const [youtubeData, setYoutubeData] = useState([]);

  useEffect(() => {
    if (spotifyToken) {
      fetchSpotifyData(spotifyToken)
        .then(data => setSpotifyData(data.items))
        .catch(err => console.error(err));
    }

    if (youtubeToken) {
      fetchYoutubeData(youtubeToken)
        .then(data => setYoutubeData(data.items))
        .catch(err => console.error(err));
    }
  }, [spotifyToken, youtubeToken]);

  // Logout function for Spotify
  const handleSpotifyLogout = () => {
    localStorage.removeItem('spotifyToken');
    setSpotifyData([]); // Clear the displayed data
    window.location.href = '/'; // Redirect to dashboard
  };

  // Logout function for YouTube
  const handleYoutubeLogout = () => {
    localStorage.removeItem('youtubeToken');
    setYoutubeData([]); // Clear the displayed data
    window.location.href = '/'; // Redirect to dashboard
  };

  return (
    <div>
      <h1>Dashboard</h1>

      {/* Display the login buttons conditionally based on the token availability */}
      {!spotifyToken && (
        <div>
          <p>You are not logged into Spotify.</p>
          <a href="http://localhost:3001/spotify/login">
            <button>Log into Spotify</button>
          </a>
        </div>
      )}

      {!youtubeToken && (
        <div>
          <p>You are not logged into YouTube.</p>
          <a href="http://localhost:3001/youtube/login">
            <button>Log into YouTube</button>
          </a>
        </div>
      )}

      {/* Display Spotify data and logout button if logged in */}
      {spotifyToken && (
        <div>
          <h2>Spotify Recently Played:</h2>
          {spotifyData.length > 0 ? (
            <ul>
              {spotifyData.map(track => (
                <li key={track.track.id}>
                  {track.track.name} by {track.track.artists[0].name}
                </li>
              ))}
            </ul>
          ) : (
            <p>No recently played tracks available.</p>
          )}
          <button onClick={handleSpotifyLogout}>Log out of Spotify</button>
        </div>
      )}

      {/* Display YouTube data and logout button if logged in */}
      {youtubeToken && (
        <div>
          <h2>YouTube Recent Videos:</h2>
          {youtubeData.length > 0 ? (
            <ul>
              {youtubeData.map(video => (
                <li key={video.id.videoId}>{video.snippet.title}</li>
              ))}
            </ul>
          ) : (
            <p>No recent videos available.</p>
          )}
          <button onClick={handleYoutubeLogout}>Log out of YouTube</button>
        </div>
      )}

      {/* If neither token is present, show a link to the login page */}
      {!spotifyToken && !youtubeToken && (
        <div>
          <p>You are not logged into any service. Please log in to Spotify or YouTube.</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
