import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Dashboard from './components/Dashboard';

function App() {
  const [spotifyToken, setSpotifyToken] = useState(null);
  const [youtubeToken, setYoutubeToken] = useState(null);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get('spotify_access_token');
    const ytToken = params.get('youtube_access_token');

    if (token) {
      setSpotifyToken(token);
      window.history.replaceState({}, document.title, window.location.pathname); // Clear the URL params
    }

    if (ytToken) {
      setYoutubeToken(ytToken);
      window.history.replaceState({}, document.title, window.location.pathname); // Clear the URL params
    }
  }, []);

  return (
    <Router>
      <Routes>
        {/* Redirects to Login page on the root URL */}
        <Route path="/" element={<Navigate to="/login" />} />
        <Route path="/login" element={<Login />} />
        <Route path="/dashboard" element={<Dashboard spotifyToken={spotifyToken} youtubeToken={youtubeToken} />} />
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
