// src/components/LoginSpotify.js
import React from 'react';

const LoginSpotify = () => {
  const loginToSpotify = () => {
    const BASE_URL = 'http://localhost:3001'; // Adjust as needed
    window.location.href = `${BASE_URL}/spotify/login`; // Redirect to your backend's Spotify login route
  };

  return (
    <div>
      <h2>Login to Spotify</h2>
      <button onClick={loginToSpotify}>Login</button>
    </div>
  );
};

export default LoginSpotify;
