import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { fetchSpotifyData, fetchYoutubeData } from '../api';

const Dashboard = ({ spotifyToken, youtubeToken }) => {
  const [spotifyData, setSpotifyData] = useState([]);
  const [youtubeData, setYoutubeData] = useState([]);

  useEffect(() => {
    if (spotifyToken) {
      fetchSpotifyData(spotifyToken)
        .then(data => setSpotifyData(data.items))
        .catch(err => console.error(err));
    }

    if (youtubeToken) {
      fetchYoutubeData(youtubeToken)
        .then(data => setYoutubeData(data.items))
        .catch(err => console.error(err));
    }
  }, [spotifyToken, youtubeToken]);

  return (
    <div>
      <h1>Dashboard</h1>

      {/* Spotify: Show most played tracks */}
      {spotifyToken && (
        <div>
          <h2>Spotify Most Played Tracks:</h2>
          {spotifyData.length > 0 ? (
            <ul>
              {spotifyData.map(track => (
                <li key={track.id}>
                  {track.name} by {track.artists.map(artist => artist.name).join(', ')}
                  <br />
                  <img src={track.album.images[0].url} alt={track.name} width="50" />
                </li>
              ))}
            </ul>
          ) : (
            <p>No top tracks available.</p>
          )}
        </div>
      )}

      {/* YouTube: Show liked videos */}
      {youtubeToken && (
        <div>
          <h2>YouTube Liked Videos:</h2>
          {youtubeData.length > 0 ? (
            <ul>
              {youtubeData.map(video => (
                <li key={video.id}>
                  <a href={`https://www.youtube.com/watch?v=${video.id}`}>
                    {video.snippet.title}
                  </a>
                  <br />
                  <img src={video.snippet.thumbnails.default.url} alt={video.snippet.title} width="50" />
                  <p>Views: {video.statistics.viewCount}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No liked videos available.</p>
          )}
        </div>
      )}

      {/* Show login options if not logged into both */}
      {!spotifyToken && (
        <a href="http://localhost:3001/spotify/login">
          <button>Log into Spotify</button>
        </a>
      )}

      {!youtubeToken && (
        <a href="http://localhost:3001/youtube/login">
          <button>Log into YouTube</button>
        </a>
      )}
    </div>
  );
};

export default Dashboard;
