import React, { useEffect } from 'react';
import { loginToSpotify, fetchSpotifyToken } from '../api/spotify';

const LoginSpotify = () => {
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const code = params.get('code');
    if (code) {
      fetchSpotifyToken(code)
        .then((data) => {
          console.log('Spotify Token:', data);
          localStorage.setItem('spotify_token', data.access_token); // Store token in local storage
        })
        .catch((err) => console.error('Error fetching Spotify token:', err));
    }
  }, []);

  return (
    <div>
      <h2>Login to Spotify</h2>
      <button onClick={loginToSpotify}>Login</button>
    </div>
  );
};

export default LoginSpotify;
