import axios from 'axios';

export const fetchSpotifyData = async (token) => {
    const response = await axios.get('https://api.spotify.com/v1/me/top/tracks', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
      params: {
        time_range: 'long_term', // Fetching all-time most played tracks (long_term covers several years)
        limit: 10, // Limit the results to 10 tracks (you can adjust as needed)
      },
    });
    return response.data;
  };


export const fetchYoutubeData = async (token) => {
  const response = await axios.get('https://www.googleapis.com/youtube/v3/search', {
    params: {
      part: 'snippet',
      maxResults: 10,
      order: 'date',
      type: 'video',
      access_token: token,
    },
  });
  return response.data;
};
